package math;

import java.util.Scanner;

public class math_10952 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner in = new Scanner(System.in);
		
		while(true){
			int a = in.nextInt();
			int b = in.nextInt();
			if(a==0&&b==0)break;
			
			System.out.println(a+b);
		}
	}

}
